// Parse incoming device messages to generate events
def parse(String description) {
    log.debug "Parsing '${description}'"

    def keyVal = description.split(":", 2)
    if (keyVal[0] in ["level", "switch"]) {
        return createEvent(name: keyVal[0], value: keyVal[1])
    } else if (keyVal[0] == "updated") {
        log.trace "Wifi Blinds was updated"
        return null
    } else {
        log.warn "Unknown event in Wifi Blinds parse(): ${description}"
        return null
    }
}

def on() {
    return setLevel(100)
}

def off() {
    return setLevel(0)
}

def setLevel(level) {
    def oldLevel = device.currentValue("level") ?: 0

    log.info("Wifi Blinds ${device.deviceNetworkId} level set to: ${level} from ${oldLevel}")
    if (level == oldLevel || level > 100 || level < 0) return null

    if (oldLevel != 0 && level == 0) {
        sendEvent(name: "switch", value: "turningOff")
    } else if (oldLevel == 0 && level != 0) {
        sendEvent(name: "switch", value: "turningOn")
    }

    def netAddr = device.currentValue("ip")
    def dni = device.deviceNetworkId

    log.info("Wifi Blinds posting to ${netAddr} for ${device.deviceNetworkId}")
    return new physicalgraph.device.HubAction([
            method: "POST",
            path: "/set",
            headers: [
                    HOST: "${netAddr}:80",
                    "Content-Length": 0
            ],
            query: [device: dni, level: "${level}"]
    ], "${dni}")
}

def refresh() {
    log.info("Wifi Blinds ${device.deviceNetworkId} refreshing at ${device.currentValue("ip")}")
    return new physicalgraph.device.HubAction([
            method: "POST",
            path: "/subscribe",
            headers: [
                    HOST: "${netAddr}:80",
                    "Content-Length": 0
            ],
            query: [device: dni]
    ], "${dni}")
}
